// http://stackoverflow.com/questions/4060004/calculate-age-in-javascript/7091965#7091965
module.exports = function getAge(dateString) {
  // Check if dateString is a string
  if (typeof dateString !== 'string') {
    console.error('Invalid date string:', dateString);
    return null; // or handle the error in an appropriate way
  }

  // Extract day, month, and year from the date string
  var parts = dateString.split('-');
  if (parts.length !== 3) {
    console.error('Invalid date format:', dateString);
    return null; // or handle the error in an appropriate way
  }

  var day = parseInt(parts[0], 10);
  var month = parseInt(parts[1], 10) - 1; // subtract 1 as months are 0-indexed
  var year = parseInt(parts[2], 10);

  var today = new Date();
  var birthDate = new Date(year, month, day);
  var age = today.getFullYear() - birthDate.getFullYear();
  var monthDiff = today.getMonth() - birthDate.getMonth();

  if (monthDiff < 0 || (monthDiff === 0 && today.getDate() < birthDate.getDate())) {
    age--;
  }

  return age;
}


